const day = document.getElementById('days');
const hour = document.getElementById('hours');
const minuts = document.getElementById('years');
const seconn = document.getElementById('seconnd');

//id.innerHTML = d ;

let d = new Date()

var dt = d.getFullYear();
var s = dt+1;
var newyears = "1 Jan "+ s;

console.log(newyears);
function timeleft()
{
    const newYear = new Date(newyears);
    const current = new Date();

    const totalseconnds =  (newYear-current) / 1000 ;

    const Days = Math.floor(totalseconnds / 3600 / 24) ;
    const Hours = Math.floor((totalseconnds/3600) % 24);
    const Minut =Math.floor( totalseconnds /60 ) % 60;
    const Seconnds = Math.floor(totalseconnds)%60;

    //console.log(Days,Hours,Minut,Seconnds);
    day.innerHTML = Days ;
    hour.innerHTML = Hours;
    minuts.innerHTML = Minut;
    seconn.innerHTML = Seconnds;
}
timeleft();
setInterval(timeleft,1000);


